import json
import os

json_data = '''
{
    "items": [
        {
            "title": "Done",
            "arg": "done",
            "icon": {
                "path": "icons/bg_colour.png"
            }
        },
        {
            "title": "Foreground Colour",
            "arg": "choose_fg",
            "icon": {
                "path": "icons/fg_colour.png"
            }
        },
        {
            "title": "Background Colour",
            "arg": "choose_bg",
            "icon": {
                "path": "icons/bg_colour.png"
            }
        },
        {
            "title": "Enable Background",
            "arg": "choose_bg",
            "icon": {
                "path": "icons/bg_colour.png"
            }
        }
    ]
}
'''

icon_paths = {
    "Foreground Colour": os.path.join(os.getenv("alfred_workflow_data"), "swatch_icons/foreground.png"),
    "Background Colour": os.path.join(os.getenv("alfred_workflow_data"), "swatch_icons/background.png"),
    "Done": "done.png"
}

data = json.loads(json_data)

for item in data['items']:
    title = item['title']
    if title in icon_paths:
        item['icon']['path'] = icon_paths[title]

new_json_data = json.dumps(data, indent=4)

print(new_json_data)

function chooseColor() {
    try {
        let app = Application.currentApplication();
        app.includeStandardAdditions = true;

        // Open the color picker with a default color of red
        let color = app.chooseColor({defaultColor: [1, 0, 0]});

        // Convert the normalized color values (0-1) to 8-bit RGB values (0-255)
        let r = Math.round(color[0] * 255);
        let g = Math.round(color[1] * 255);
        let b = Math.round(color[2] * 255);

        // Convert RGB values to HEX
        return rgbToHex(r, g, b);
    } catch (error) {
        return "no_swatch";
    }
}

function rgbToHex(r, g, b) {
    return ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1).toUpperCase();
}

chooseColor();

// Set the main variable in Configuration Sheet

ObjC.import('stdlib');
