    toValue: $.getenv('selected_colour'),
    inWorkflow: $.getenv('alfred_workflow_bundleid'),
    exportable: true
});


// output selected_colour without the '#' for use in next workflow objects
$.getenv('selected_colour').replace(/#/g, "");

